import React, { useEffect } from 'react'
import axios from 'axios'
import './index.css'
import Navbar from './Navbar'

function Latest_news() {
    const [news, setNews] = React.useState([])

    useEffect(() => {

        axios.get('http://newsapi.org/v2/everything?q=bitcoin&from=2020-11-28&sortBy=publishedAt&apiKey=4c0f69f361ed489aa8cc629509445e36')
            .then(res => {
                console.log(res);
                setNews(res.data)
            })
            .catch(err => {
                console.log(err);
            })
    }, [])
    console.log(news, "yyy");
    return (
        <>
            <Navbar />

            {
                news.articles && news.articles.map(post => (
                    // <li key={post.id}> </li>
                    <div className="card" style={{ width: "55rem", height: "50rem", textAlign: "center" }}>
                        <img src={post.urlToImage} className="card-img-top" alt="..."></img>
                        <div className="card-body">
                            <h5 className="card-title"><ol><li>{post.title}</li></ol></h5>
                            <p className="card-text"><p>{post.description}.</p></p>
                            <p className="auther-name"><img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShKInw3jM-VnjBNK6Z4XZ8kw8GH-NyNpGubw&usqp=CAU" className="author" /> :<i>{post.author}</i> </p>
                            <p href="#" class="btn btn-primary">{post.publishedAt} </p>
                            <br/><br/>
                        </div>
                    </div>
                ))}
        </>

    )
}

export default Latest_news;
